echo "Installing public chaincode on each peer"
CORE_PEER_ADDRESS=peer0.greenairline.com:7051  CORE_PEER_LOCALMSPID="GreenAirlineMSP"  CORE_PEER_MSPCONFIGPATH=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/greenairline.com/users/Admin@greenairline.com/msp   CORE_PEER_TLS_ROOTCERT_FILE=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/greenairline.com/peers/peer0.greenairline.com/tls/ca.crt   peer chaincode install -n bspcc -v 1.0 -p github.com/hyperledger/fabric/examples/chaincode/go/chaincode_example02
CORE_PEER_ADDRESS=peer0.greenagencies.com:7051 CORE_PEER_LOCALMSPID="GreenAgenciesMSP" CORE_PEER_MSPCONFIGPATH=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/greenagencies.com/users/Admin@greenagencies.com/msp CORE_PEER_TLS_ROOTCERT_FILE=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/greenagencies.com/peers/peer0.greenagencies.com/tls/ca.crt peer chaincode install -n bspcc -v 1.0 -p github.com/hyperledger/fabric/examples/chaincode/go/chaincode_example02
