echo "Initializing bsp chaincode for channel green. It is only done once by a single peer."
CORE_PEER_ADDRESS=peer0.bsps.com:7051 CORE_PEER_LOCALMSPID="BspsMSP" CORE_PEER_MSPCONFIGPATH=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/bsps.com/users/Admin@bsps.com/msp CORE_PEER_TLS_ROOTCERT_FILE=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/bsps.com/peers/peer0.bsps.com/tls/ca.crt peer chaincode instantiate -o orderer1:7050 --tls --cafile /opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/ordererOrganizations/orderer.com/orderers/orderer1.orderer.com/tls/ca.crt -C green -n bspcc -v 1.0 -c '{"Args":["init","a","100","b","200"]}' -P "OutOf(2, 'BspsMSP.member', 'GreenAirlineMSP.member', 'GreenAgenciesMSP.member')" --collections-config /opt/gopath/src/github.com/hyperledger/fabric/peer/private-collections/green-private.json

echo "Initializing bsp chaincode for channel blue. It is only done once by a single peer."
CORE_PEER_ADDRESS=peer0.bsps.com:7051 CORE_PEER_LOCALMSPID="BspsMSP" CORE_PEER_MSPCONFIGPATH=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/bsps.com/users/Admin@bsps.com/msp CORE_PEER_TLS_ROOTCERT_FILE=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/bsps.com/peers/peer0.bsps.com/tls/ca.crt peer chaincode instantiate -o orderer1:7050 --tls --cafile /opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/ordererOrganizations/orderer.com/orderers/orderer1.orderer.com/tls/ca.crt -C blue  -n bspcc -v 1.0 -c '{"Args":["init","a","100","b","200"]}' -P "OutOf(2, 'BspsMSP.member', 'BlueAirlineMSP.member', 'BlueAgenciesMSP.member' )"
